export default {
  name: "writeContent",
  components: {
    editor
  },
  data() {
    return {
      title: "",
      content: "",
      isShowEditor: true,
      options: {
        delay: 0,
        targetBlank: true,
        toolbar: {
          allowMultiParagraphSelection: true,
          // buttons: ['bold', 'italic', 'underline', 'anchor', 'h2', 'h3', 'quote'],
          buttons: buttonList,
          diffLeft: 0,
          diffTop: -10,
          firstButtonClass: 'medium-editor-button-first',
