          unwrapTags: ['sub', 'sup']
        },
        anchorPreview: {
          hideDelay: 300
        },
        placeholder: {
          text: 'please enter you Title'
        }
      },
      optionsContent: {
        delay: 0,
        targetBlank: true,
        toolbar: {
          allowMultiParagraphSelection: true,
          // buttons: ['bold', 'italic', 'underline', 'anchor', 'h2', 'h3', 'quote'],
          buttons: buttonList,
          diffLeft: 0,
          diffTop: -10,
          firstButtonClass: 'medium-editor-button-first',
          lastButtonClass: 'medium-editor-button-last',
          relativeContainer: null,
          standardizeSelectionStart: true,
          static: true,
          /* options which only apply when static is true */
          align: 'left',
          sticky: true,
          updateOnEmptySelection: false
        },
        anchor: {
          placeholderText: 'Type a link',
          customClassOption: 'btn',
          customClassOptionText: 'Create Button'
        },
        paste: {
          cleanPastedHTML: true,
          cleanAttrs: ['style', 'dir'],
          cleanTags: ['label', 'meta'],
          unwrapTags: ['sub', 'sup']
        },
        anchorPreview: {
          hideDelay: 300
        },
        placeholder: {
          text: 'please enter you Content'
        }
      },
      applyTextEdit: undefined,
      pay: Everpay
    };
  },
  computed: {
    ...mapGetters({
      accountStr: 'account/account'
    }),
    accountColor() {
      if (!this.accountStr) return '#ffffff';
      const str = strToHexCharCode(strToUtf8Bytes(this.accountStr));
      return {
        top: `#${str.slice(0, 6)}`,
        bottom: `#${str.substring(str.length - 6)}`
      };
    },
    accountString() {
      return `${this.accountStr.slice(0, 4)}......${this.accountStr.substring(this.accountStr.length - 4)}`;
    }
  },
  methods: {
    async combineFileList() {
      const fee = await getBundleFee(reportUrl, this.content.length, this.selectedSymbol);
      const formatedFee = new Bignumber(fee.finalFee).dividedBy(new Bignumber(10).pow(fee.decimals)).toString();
      if (+this.balance >= +formatedFee) {
        const data = this.content;
        const ops = {
          tags: [{
            name: "FileName",
            value: this.title
          }, {
            name: "Content-Type",
            value: 'application/json'
          },
          // The official version has two new fixed tags
          {
            name: "APP-Name",
            value: "Msc-Project"
          },
          // Facilitate adot to obtain data and display increased
          {
            name: "file-size",
            value: String(this.content.length)
          }]
        };
        if (!this.sendApi) {
          this.sendApi = await genAPI(window.ethereum);
        }
        try {
          //
          const res = await this.sendApi.sendAndPay(reportUrl, data, this.selectedSymbol, ops);
          this.submitResp = JSON.stringify(res);
          console.log(this.submitResp);
        } catch (e) {
          this.$message.error(e.message);
        }
        await this.getOrders(this.accountStr);
        await this.getList();
      } else {
        alert(`need ${formatedFee} ${this.selectedSymbol} to upload`);
      }
    },
    handleChangeTitle(e) {
      this.title = e.target.innerHTML;
    },
    handleChangeContent(e) {
      this.content = e.target.innerHTML;
    },
    async BackTo() {
      this.$router.push({
        name: "Main"
      });
    }
  }
