import EchoButton from '@0xecho/button';
export default {
  name: "commentDialog",
  methods: {
    init() {
      const echo = new EchoButton({
        targetUri: 'https://0xecho.com',
        // commenting target, required
        alwaysShowPopover: true,
        // whether always show popover, default: false
        partnerName: '',
        // if specified, partner name will be shown on popover
        numberType: 'power',
        // button display number type, power(default) or count,
        theme: 'light' // dark or light(default)
      }).mount('#echo-button-light');
      echo.destroy();
    }
  }
};
