        // 币种的小数位
        fee: "",
        // 金额，费用
        paymentStatus: "",
        // 订单支付状态（unpaid---waiting, paid---pending,success, expired---failed）
        paymentId: "",
        // string，订单id
        onChainStatus: "" // 是否上链（waiting，pending, success, failed）
      },

      // signer: Signer,
      pay: Everpay
    };
  },
  computed: {
    ...mapGetters({
      signer: 'account/signerInfo',
      accountStr: 'account/account'
    }),
    accountColor() {
      if (!this.accountStr) return '#ffffff';
      const str = strToHexCharCode(strToUtf8Bytes(this.accountStr));
      return {
        top: `#${str.slice(0, 6)}`,
        bottom: `#${str.substring(str.length - 6)}`
      };
    },
    accountString() {
      return `${this.accountStr.slice(0, 4)}......${this.accountStr.substring(this.accountStr.length - 4)}`;
    }
  },
  async mounted() {
    // if (this.$route.query.id) {
