    //   const data = await this.getMeta(itemId)
    //   this.title = data.content.title
    //   this.content = data.content.body
    // }
  },
  methods: {
    ...mapActions({
      getMeta: 'order/getItemMetaInfo'
    }),
    async save() {
      const tokenSymbol = "AR";
      const originalContentDigest = "";
      const ord = await submitToArseed(tokenSymbol, this.signer, this.title, this.content, originalContentDigest);
      this.orderInfo = {
        itemId: ord.itemId,
        bundler: ord.bundler,
        size: ord.size,
        currency: ord.currency,
        decimals: ord.decimals,
        fee: ord.fee,
        paymentStatus: "unpaid"
      };
    },
    async combineFileList() {
      const fee = await getBundleFee(reportUrl, this.content.length, this.selectedSymbol);
      const formatedFee = new Bignumber(fee.finalFee).dividedBy(new Bignumber(10).pow(fee.decimals)).toString();
      if (+this.balance >= +formatedFee) {
        const data = this.content;
        const ops = {
          tags: [{
            name: "FileName",
            value: this.title
          }, {
            name: "Content-Type",
            value: 'application/json'
          },
          // The official version has two new fixed tags
          {
            name: "APP-Name",
            value: "Msc-Project"
          },
          // Facilitate adot to obtain data and display increased
          {
            name: "file-size",
            value: String(this.content.length)
          }]
        };
        if (!this.sendApi) {
          this.sendApi = await genAPI(window.ethereum);
        }
        try {
          //
          const res = await this.sendApi.sendAndPay(reportUrl, data, this.selectedSymbol, ops);
          this.submitResp = JSON.stringify(res);
          this.$refs['uploadFile'].cancelUpload();
          console.log(this.submitResp);
        } catch (e) {
          this.$message.error(e.message);
          this.$refs['uploadFile'].cancelUpload();
        }
        await this.getOrders(this.accountStr);
        await this.getList();
      } else {
        alert(`need ${formatedFee} ${this.selectedSymbol} to upload`);
      }
    },
    async payBill() {
      // 点击 submit 调用这个方法
      await this.testPay();
      payOrder(this.pay, this.orderInfo).then(tx => {
        if (tx) {
          this.$router.push({
            name: 'List'
          });
        }
      }).catch(() => {
        this.$message.error('submit failed');
      });
    },
    handleChangeTitle(e) {
      this.title = e.target.innerHTML;
    },
    handleChangeContent(e) {
      this.content = e.target.innerHTML;
    },
    async submit() {
      await this.save();
      await this.payBill();
    },
    async BackTo() {
      this.$router.push({
        name: "Main"
      });
    },
    async testSigner() {
      // await window.ethereum.enable()
      // const provider = new providers.Web3Provider(window.ethereum)
